func TestHackerNews(t *testing.T) {
	filename := filepath.Join(os.TempDir(), "hackernews-feed-test.bolt")

	os.Remove(filename)

	var (
		config = db.NewBoltConfig(filename)
		client = db.NewClient(config)
	)

	if err := client.Open(); err != nil {
		t.Fatal(err)
	}
	defer func() {
		if err := client.Close(); err != nil {
			t.Fatal(err)
		}
	}()
	defer os.Remove(filename)

	s := httptest.NewServer(http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
		io.WriteString(w, hackerNewsTestData)
	}))
	defer s.Close()

	HackerNewsAPIURL = s.URL + "/x?perPage=%v"

	ds := NewHackerNews(client)

	possiblePkgs, err := ds.Refresh()
	if err != nil {
		t.Fatal(err)
	}
	t.Logf("possiblePkgs(%v)=%+v", len(possiblePkgs), possiblePkgs)
	if len(possiblePkgs) < 10 {
		t.Errorf("Expected len(possiblePkgs) >= 10 but actual=%v", len(possiblePkgs))
	}
}

